import pandapower.networks as ppn


def get_meas_info(target='boundary', redundancy='high', pq_std_dev=0.01, v_std_dev=0.01, i_std_dev=0.01):
    assert target.lower() in ['boundary', 'internal'], "Invalid target setting."
    assert redundancy.lower() in ['high', 'low'], "Invalid redundancy setting."
    meas_info = []
    if target.lower() == 'boundary':
        if redundancy.lower() == 'high':
            meas_info.append({'meas_type': 'P', 'element_type': 'bus',
                              'element': 'boundary', 'std_dev': pq_std_dev})
            meas_info.append({'meas_type': 'Q', 'element_type': 'bus',
                              'element': 'boundary', 'std_dev': pq_std_dev})
            meas_info.append({'meas_type': 'V', 'element_type': 'bus',
                              'element': 'boundary', 'std_dev': v_std_dev})
            meas_info.append({'meas_type': 'I', 'element_type': 'line',
                              'side': 'both', 'element': 'boundary', 'std_dev': i_std_dev})
            meas_info.append({'meas_type': 'P', 'element_type': 'line',
                              'side': 'both', 'element': 'boundary', 'std_dev': pq_std_dev})
            meas_info.append({'meas_type': 'Q', 'element_type': 'line',
                              'side': 'both', 'element': 'boundary', 'std_dev': pq_std_dev})
        else:
            meas_info.append({'meas_type': 'V', 'element_type': 'bus',
                              'element': 'boundary', 'std_dev': v_std_dev})
            meas_info.append({'meas_type': 'I', 'element_type': 'line',
                              'side': 'from', 'element': 'boundary', 'std_dev': i_std_dev})
            meas_info.append({'meas_type': 'P', 'element_type': 'line',
                              'side': 'from', 'element': 'boundary', 'std_dev': pq_std_dev})
            meas_info.append({'meas_type': 'Q', 'element_type': 'line',
                              'side': 'from', 'element': 'boundary', 'std_dev': pq_std_dev})
    else:
        if redundancy.lower() == 'high':
            meas_info.append({'meas_type': 'P', 'element_type': 'bus',
                              'element': 'internal', 'std_dev': pq_std_dev})
            meas_info.append({'meas_type': 'Q', 'element_type': 'bus',
                              'element': 'internal', 'std_dev': pq_std_dev})
            meas_info.append({'meas_type': 'V', 'element_type': 'bus',
                              'element': 'internal', 'std_dev': v_std_dev})
            meas_info.append({'meas_type': 'I', 'element_type': 'line',
                              'side': 'both', 'element': 'internal', 'std_dev': i_std_dev})
            meas_info.append({'meas_type': 'P', 'element_type': 'line',
                              'side': 'both', 'element': 'internal', 'std_dev': pq_std_dev})
            meas_info.append({'meas_type': 'Q', 'element_type': 'line',
                              'side': 'both', 'element': 'internal', 'std_dev': pq_std_dev})
            meas_info.append({'meas_type': 'I', 'element_type': 'trafo',
                              'side': 'both', 'element': 'internal', 'std_dev': i_std_dev})
            meas_info.append({'meas_type': 'P', 'element_type': 'trafo',
                              'side': 'both', 'element': 'internal', 'std_dev': pq_std_dev})
            meas_info.append({'meas_type': 'Q', 'element_type': 'trafo',
                              'side': 'both', 'element': 'internal', 'std_dev': pq_std_dev})
        else:
            meas_info.append({'meas_type': 'V', 'element_type': 'bus',
                              'element': 'internal', 'std_dev': v_std_dev})
            meas_info.append({'meas_type': 'I', 'element_type': 'line',
                              'side': 'from', 'element': 'internal', 'std_dev': i_std_dev})
            meas_info.append({'meas_type': 'P', 'element_type': 'line',
                              'side': 'from', 'element': 'internal', 'std_dev': pq_std_dev})
            meas_info.append({'meas_type': 'Q', 'element_type': 'line',
                              'side': 'from', 'element': 'internal', 'std_dev': pq_std_dev})
            meas_info.append({'meas_type': 'I', 'element_type': 'trafo',
                              'side': 'hv', 'element': 'internal', 'std_dev': i_std_dev})
            meas_info.append({'meas_type': 'P', 'element_type': 'trafo',
                              'side': 'hv', 'element': 'internal', 'std_dev': pq_std_dev})
            meas_info.append({'meas_type': 'Q', 'element_type': 'trafo',
                              'side': 'hv', 'element': 'internal', 'std_dev': pq_std_dev})
    return meas_info


def case118():
    """
    IEEE 118-bus test system parameters
    """
    net = ppn.case118()
    zone_info = dict()
    zone_info[1] = set(range(17)) | {29, 116}
    zone_info[2] = set(range(20, 29)) | {30, 31} | set(range(69, 73)) | set(range(112, 115))
    zone_info[3] = set(range(17, 20)) | set(range(32, 48))
    zone_info[4] = set(range(48, 64))
    zone_info[5] = set(range(73, 77)) | set(range(81, 96)) | {101, 117}
    zone_info[6] = set(range(64, 69)) | set(range(77, 81)) | set(range(96, 101)) | set(range(102, 112)) | {115}
    reference_bus = [9, 25, 45, 49, 90, 68]
    default_meas = [*get_meas_info(target='boundary', redundancy='low'),
                    *get_meas_info(target='internal', redundancy='high')]
    return {'id': 'IEEE118', 'ppnet': net, 'zone_info': zone_info,
            'ref_bus': reference_bus, 'default_meas': default_meas}


def case300():
    """
    IEEE 300-bus test system parameters
    """
    net = ppn.case300()
    zone_info = dict()
    zone_info[1] = set(range(93)) | {179, 185} | set(range(230, 300)) - set(range(241, 246)) - set(range(262, 265))
    zone_info[2] = set(range(93, 167)) | set(range(242, 245)) | set(range(262, 265))
    zone_info[3] = set(list(range(0, 300))) - zone_info[1] - zone_info[2]
    reference_bus = [97, 191, 256]
    default_meas = [*get_meas_info(target='boundary', redundancy='low'),
                    *get_meas_info(target='internal', redundancy='high')]
    return {'id': 'IEEE300', 'ppnet': net, 'zone_info': zone_info,
            'ref_bus': reference_bus, 'default_meas': default_meas}


